package com.bptn.course._03_flow_control._03_if_statement;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 4;
		switch (day) {
		
		case 1 : 
			System.out.print("Monday");
			break;
			
		case 4 :
			System.out.print("Thurdays");
			break;
			
		default:
		     System.out.print("Anyday");
		     
		}
		
	}

}
